package web

import (
	"encoding/base64"
	"fmt"
	"net/http"
)

func JsonResponseHandle(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "application/json; charset=utf-8")
	w.Write([]byte(`{"Name":"Arick",Hobbies":["snowboarding","programming"]}`))
}

func XmlResponseHandle(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "application/xml; charset=utf-8")
	w.Write([]byte(`<Profile>
  <Name>Alex</Name>
  <Hobbies>
    <Hobby>snowboarding</Hobby>
    <Hobby>programming</Hobby>
  </Hobbies>
</Profile>`))
}

func HtmlResponseHandle(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/html; charset=utf-8")
	w.Write([]byte(`<h1>Hello Web</h1>`))
}

func TextResponseHandle(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/plain; charset=utf-8")
	w.Write([]byte(`Hello Web`))
}

func GifResponseHandle(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "image/gif")
	d, err := base64.StdEncoding.DecodeString(``)
	if err != nil {
		panic(err)
	}
	w.Write(d)
}

func JpegResponseHandle(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "image/jpeg")
	d, err := base64.StdEncoding.DecodeString(``)
	if err != nil {
		panic(err)
	}
	w.Write(d)
}

func PngResponseHandle(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "image/png")
	d, err := base64.StdEncoding.DecodeString(``)
	if err != nil {
		panic(err)
	}
	w.Write(d)
}

func download(w http.ResponseWriter, r *http.Request, mime, filename, data string) {
	w.Header().Set("Content-Type", mime)
	w.Header().Set("Content-Disposition", fmt.Sprintf(`attachment; filename="%s"`, filename))
	d, err := base64.StdEncoding.DecodeString(data)
	if err != nil {
		panic(err)
	}
	w.Write(d)
}

func FileResponseHandle(w http.ResponseWriter, r *http.Request) {
	download(w, r, "application/octet-stream", "general.png", ``)
}

func XlsResponseHandle(w http.ResponseWriter, r *http.Request) {
	download(w, r, "application/vnd.ms-excel", "general.xls", ``)
}

func XlsxResponseHandle(w http.ResponseWriter, r *http.Request) {
	download(w, r, "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet", "general.xlsx", ``)
}

func RegisterMimeHandler() {
	http.HandleFunc("/mime/xml", XmlResponseHandle)
	http.HandleFunc("/mime/json", JsonResponseHandle)
	http.HandleFunc("/mime/html", HtmlResponseHandle)
	http.HandleFunc("/mime/text", TextResponseHandle)
	http.HandleFunc("/mime/png", PngResponseHandle)
	http.HandleFunc("/mime/jpeg", JpegResponseHandle)
	http.HandleFunc("/mime/gif", GifResponseHandle)
	http.HandleFunc("/mime/file", FileResponseHandle)
	http.HandleFunc("/mime/xls", XlsResponseHandle)
	http.HandleFunc("/mime/xlsx", XlsxResponseHandle)
}
